html,body,button,input,select,textarea,table,th,td,form,h1,h2,ul,li,img,dl,dd{ margin: 0; padding: 0; font: normal normal 12px/14px Microsoft YaHei,Arail;}
table { border-collapse: collapse; border-spacing: 0;}
table th,table td { padding: 0; empty-cells: show; word-break: break-all; word-wrap: break-word;}
a { text-decoration: none; color: #3c90ee;}
a:hover { text-decoration: none; color: #f35f06;}
img { display: block; border:none;}
ul,li{ list-style:none;}
.clearfix:before,
.clearfix:after { content: " "; display: table;}
.clearfix:after { clear: both;}
.clearfix { *zoom: 1;}
a:focus, input { outline: none;}
code.lang { display: none;}
.hide, .lowMCU{ display: none!important; }
/*layout*/
body { background: url(../images/bg_header.png) repeat-x #dbdbdb; }
#header { width: 100%; min-width: 1200px; height: 75px;}
#footer { width: 100%; min-width: 1200px; height: 40px; background-color: #cbcbcb; clear: both;}
.wrap { width: 100%;}
.subnav { position: relative; float: left; display: inline; width: 70px; height: 735px; background: #2e5273;}
.subnav2 { position: absolute; left: 0; top: 75px; width: 190px; height: 735px; background: #42678a; overflow: hidden;}
.subnav2 .shadow { position: absolute; left: 0; top: 0; width: 100%; height: 3px; background: url(../images/bg_shadow.png) repeat-x;}
.submain { position: relative; margin-left: 190px; height: 732px; padding-top: 3px; background: url(../images/bg_submain.png) repeat-x #fff;}
.submaintop { height: 67px; background-color: #ebebeb;}
.pr { position: relative;}

