/*header*/
#header .top { height: 25px;}
#header .top .fr { height: 20px; margin-top: 4px; margin-right: 10px; color: #fff; }
.top .home, .top .user { float: left; display: inline; height: 16px; overflow: hidden; padding-right: 12px; margin-right: 12px;}
.top .home i, .top .user i { float: left; display: inline; height: 14px; overflow: hidden; background: url(../images/icons.png) no-repeat -3px -3px; margin-right: 10px;}
.top .home i { width: 16px; background-position: -82px -3px;}
.top .user i { width: 14px; background-position: -3px -3px;}
.top .sep { border-right: 1px solid #414342;}
.top .logout, .top .nowTime { float: left; display: inline; height: 20px; line-height: 16px;}
.top .nowTime {padding-right: 12px; margin-right: 12px;}
#header .head { height: 50px; min-width: 1035px;}
.logo { background:url("../images/logo_mini.png") no-repeat 6px 4px; float: left; display: inline; width: 187px; height: 34px; line-height: 16px; padding-left: 62px; padding-top: 16px; font-size: 16px; color: #fff;}
#header .menu { float: left; display: inline;}
#header .menu li { float: left; display: inline; padding: 0 37px; margin-top: 16px; height: 18px; border-right: 1px solid #4e81c3;}
#header .menu li a { display: block; font-size: 14px; color: #78A3FF;
    -webkit-transition: color 0.5s;-moz-transition: color 0.5s;-ms-transition: color 0.5s;-o-transition: color 0.5s;transition: color 0.5s;
}
#header .menu li a.cur, #header .menu li a:hover { color: #fff;}

