.tablist a { position: relative; height: 24px; float: left; display: inline; color: #343434; margin-right: 24px;text-align: center;}
.tablist a i { position: absolute; width: 11px; height: 6px; left: 50%; margin-left: -5px; bottom: -1px; overflow: hidden; display: none; background: url(../images/icons.png) no-repeat -25px -1px;}
.tablist a.cur { color: #3c96ec;}
.tablist a.cur i { display: block;}
.tabdiv { visibility: hidden;}

/* submain top tab */
.submaintopTabs {}
.submaintopTabs .tab { position: relative; float: left; display: inline; height: 39px; padding:  28px 38px 0; color: #333; font-size: 16px;
    /*    -webkit-transition: background 0.5s;-moz-transition: background 0.5s;-ms-transition: background 0.5s;-o-transition: background 0.5s;transition: background 0.5s;*/
}
.submaintopTabs .tab:hover, .submaintopTabs .cur { height: 38px; background: #d1dbe2; border-bottom: 1px solid #00adf4; color: #0079c2;}
.submaintopTabs .cur { cursor: default;}
.submaintopTabs .cur i { position: absolute; width: 21px; height: 11px; overflow: hidden; left: 50%; margin-left: -10px; bottom: -1px; background: url(../images/icons.png) no-repeat -161px -371px;}

/* table */
.table1, .table2 { width: 100%; border: 1px solid #c9c9c9;}
.table1 th, .table2 th{ height: 35px; line-height: 35px; padding-left: 12px; background: #d1dbe2; text-align: left;}
.table1 td, .table2 td { padding: 8px 0; line-height: 18px; padding-left: 12px; text-align: left; background: #fff; border-bottom: 1px solid #dfdfdf;}
/*.table1 .even td { background: #eee}*/
.table1 .hover td { background: #44b6f9; color: #fff;}
.table1 .tool_edit, .table1 .tool_del { margin-top: 0; }
.table2 .hover.hover_tr td { background: #44b6f9; color: #fff; }
.table2 .nest_td { padding: 8px; }

.table_border_none, .table_border_none th, .table_border_none td { border: none;}

