    //conftype 2预约会议；3收藏会议；4历史会议
    Meeting.getDevHtml = function(conftype,owner,room){
        var _html = "";
        var devid = room.devid;
        var _classLi = "";
        if(conftype == 3 || conftype == 4) _classLi = " null";
        var _name = "";
        if(devid.substring(0, 2) == "14"){//如果是14开头的级联平台，只有platformName，没有name
            _classLi += " remotePlatform";
            _name = htmlEncode(room.platformName == undefined ? LANG.getDisplayText("meeting_dev_not_exist") : htmlEncode(room.platformName));
        }else if(devid.substring(0, 2) == "13") {//上级平台
            _classLi += " remotePlatform";
            _name = htmlEncode(LANG.getDisplayText("higherLevel") + ":" + room.remotePlatformIP);
        }else{
            _name = room.name == undefined ? LANG.getDisplayText("meeting_dev_not_exist") : htmlEncode(room.name);
		}
		//判断会场状态 0离线； 1挂断； 2拨号（已入会）
		var status = '';
		switch (room.status) {
			case 0:
				status = 'off';
				break;
			case 1:
				status = '';
				break;
			case 2:
				status = 'in';
				break;
			default: status = 'off';
				break;
		}
		var ownerType = owner.slice(0, 2);
		if (owner == devid && ownerType != '13') {
			status += ' m';
		}
		_html += '<li id="dev_' + devid + '" class="' + status + _classLi + '">';
		if (owner == devid && ownerType != '13') {
			_html += '<span class="chairman">（'+LANG.getDisplayText("meeting_main_hall")+'）</span>';
		}
        _html += '<i class="icon"></i><i class="micon"></i>';
        _html += '<span class="kbs"></span>';
        _html += '<span class="name">'+_name+'</span>';
        _html += '<div class="hoverbg"></div>';
        var _display = [];
        // 设置主席控制
        if(conftype == 1 || conftype == 2){
            //状态：设置主席
            if(room.devid == owner){
                _display[0] = "block";
                _display[1] = "none";
            }
            else{
                _display[0] = "none";
                _display[1] = "block";
            }
            _html += '<a href="javascript:;" class="chairmanOn tools" title="'+LANG.getDisplayText("click")+'：'+LANG.getDisplayText("meeting_cancel_chairman")+'" style="display: '+_display[0]+'">'+LANG.getDisplayText("meeting_cancel_chairman")+'</a>';
            _html += '<a href="javascript:;" class="chairmanOff tools" title="'+LANG.getDisplayText("click")+'：'+LANG.getDisplayText("meeting_set_chairman")+'" style="display: '+_display[1]+';">'+LANG.getDisplayText("meeting_set_chairman")+'</a>';
        }
        _html += '<input class="dev_id" type="hidden" value="'+devid+'"></li>';
        return _html;
    };

    Meeting.getHeadMeetingTime = function(strStartTime,strEndTime){
        return [strStartTime,strEndTime];
    };

    Meeting.curPageListEmpty = function(){
