            var __time = (new Date(_endtime.replace(/\-/g,"\/")) - new Date(_starttime.replace(/\-/g,"\/")))/(1000*60);
            var _timelen = timeToDHMS(new Date(_endtime.replace(/\-/g,"\/")) - new Date(_starttime.replace(/\-/g,"\/")));
            if(__time >= 59999940){
