            var _creator = htmlEncode(_data.creatorName == undefined || _data.creatorName == "" ? LANG.getDisplayText("unknown") : _data.creatorName);
            var meetingName = htmlEncode(_data.name);
                _html += '<h2 class="fl pr10 ellipsis" title="'+_data.name.replace(/"/g,"&quot;")+'：'+seDate[0]+' ~ '+seDate[1]+'"><span class="title">'+meetingName+'</span></h2>';
            _html += '<div class="fl pr10 ellipsis" style="width: 10%;">'+_creator+'</div>';
