        var adminid = loginUsername;
		$('.contentList').delegate('.meeting_box .head', 'click', function(e) {
			var _target = $(e.target);
            if(_target.hasClass("start")) { Meeting.Control.start(_target); }
            else if(_target.hasClass("editstart")) { Meeting.Control.editstart(_target); }
            else if(_target.hasClass("end")) { Meeting.Control.end(_target); }
            else if(_target.hasClass("delay")) { Meeting.Control.delay(_target); }
            else if(_target.hasClass("edit")) { Meeting.Control.edit(_target); }
            else if(_target.hasClass("qualityReport")) { Meeting.Control.qualityReport(_target); }
            else if(_target.hasClass("broadcast")) { Meeting.Control.broadcast(_target); }
            else if(_target.hasClass("record")) { Meeting.Control.record(_target); }
            else if(_target.hasClass("sysInfo")) { Meeting.Control.sysInfo(_target); }
            else if(_target.hasClass("del")) { Meeting.Control.del(_target); }
            else if(_target.hasClass("fav")) { Meeting.Control.fav(_target); }
            else if(_target.hasClass("checkbox")) { Meeting.Control.checkbox(_target); }
            else if(_target.hasClass("meetingExport")) { Meeting.Control.meetingExport(_target); }
            else if(_target.hasClass("calledRepeat")) { Meeting.Control.calledRepeat(_target); }
            else{
                var jThis = $(this);
				var meeting_conftype = jThis.parent().find(".meeting_conftype").val();
                var jThat = jThis.next();
                if(jThat.is(":hidden")){
					jThat.show();
                }
                else{
                    jThat.hide();
                }
                if(meeting_conftype == 1) {
                    meetingTimer.updateData();
                }
            }
			e.target.blur();
		})
