                content: ['/meetingCreate?adminid='+adminid,"auto"]
			});
        }).delegate(Meeting.config.idCreateMeetingTemplate,"click",function(){// 创建常用会议
                openWinPage({
                title: LANG.getDisplayText("meeting_create_template"),
