                content: ['/meetingCreate?url=/meetingFav&conftype=3&adminid='+adminid,"auto"]
            });
        }).delegate(Meeting.config.idBtnSearch,"click",function(){// 会议搜索
            var jKeyword = $(Meeting.config.idInputKeyword);
            var keyword = getInputValue(jKeyword);
            var keywordPre = jKeyword.attr("data-pre") || "";
            if(keyword == ""){
                if(keywordPre != ""){
                    jKeyword.attr("data-pre",keyword);
                    $("#pageCur").val(0);
                    Meeting.search(Meeting.config.options);
