	Meeting.differOrder = function(confid, data) {
		var _data = data.data;
		var $li = $('#meeting_' + confid + ' .new_attend_box');
		var newOrder = {};
		if (_data) {
			var allAttendees = _data.members.all;
			for (var i=0; i<allAttendees.length; i++) {
				var obj = allAttendees[i];
				var newDevid = $li.eq(i).attr('devid');
				if (obj.clientID != newDevid) {
					newOrder[i] = newDevid;
				}
			}
		}
		return newOrder;
	};

