            scroll: scroll,
			helper: 'clone',
			containment: '#meeting_' + confid + ' .attend_ul_wrap',
			revert: true
        });
	};
    
    Meeting.searchResult = function(confid, devids){
        var $meetingBox = $("#meeting_" + confid),
            $searchWrap = $meetingBox.find('.search-wrap'),
            $searchContent = $searchWrap.find('.search-content');

        $searchContent.addClass('hide').find('li').remove();
        devids = devids.sort(function (a, b) {
            var apID = a.remotePlatformID,
                bpID = b.remotePlatformID;

            if(apID === bpID){
                return a.name.localeCompare(b.name);
            } else {
                return apID - bpID;
            }
        })

        var len = devids.length;
        if(len === 0){
            $searchContent.next().removeClass('hide');
            $searchWrap.data('focus') && $searchWrap.removeClass('hide');
            return
        } else {
            $searchContent.next().addClass('hide');
        }

        for(var i = 0; i < len; i++){
            var $devLi = $('#J_cloneSearchDev li').clone(),
                devData = devids[i];

            var remotePlatformID = devData.remotePlatformID,
                rPIDLen = remotePlatformID? remotePlatformID.length: 0,
                platformID = '',
                sup_name = ['本机'];
            for(var j = 2; platformID.length < rPIDLen; j += 2){
				platformID = remotePlatformID.slice(0, j);
				var node = meetingControl.zTreeObj[confid].getNodeByParam('platformId', platformID, null);
                sup_name.push(node.platformName);
            }

            var status = devData.status === 2 ? 'hasAttendbg': (devData.status === 1? 'onlinebg': 'offlinebg');
            $devLi.find('.meeting_point').addClass(status)
                .end().find('.search_dev_name').text(devData.name)
                .end().find('.search_dev_sup').text(sup_name.join(' - '));

			var orgid = devData.orgid;
            if(remotePlatformID){
				var node = meetingControl.zTreeObj[confid].getNodeByParam('platformId', remotePlatformID, null);
				var remoteConfid = node.remoteConfID;
                var attr = {
                        'devid': confid + '_' + remotePlatformID + '_' + remoteConfid + '_' + devData.devid,
						remoteplatformid: remotePlatformID,
						orgid: orgid
                    }
            } else {
                var attr = {
                    'devid': devData.devid,
                    remoteplatformid: '',
					orgid: orgid
				}
            }

            (function(attr){
                $devLi.attr(attr).draggable({
                    appendTo: $meetingBox,
                    cursorAt: {top: 50, left: 45},
                    scroll: false,
                    helper: function () {
                        var $this = $(this),
                            name = $this.find('.search_dev_name').text(),
                            $meetingPoint = $this.find('.meeting_point'),
                            hasAttend = $meetingPoint.hasClass('hasAttendbg')? 'hasAttended_icon': ($meetingPoint.hasClass('onlinebg')? 'online_icon': ''),
                            $li = $('#J_cloneAttend li').clone();

                        $li.attr(attr)
                            .find('.def_icon').addClass(hasAttend)
                            .siblings('.h_name').text(name).end();

                        return $li;
                    },
                    start: function () {
                        $searchWrap.addClass('hide')
                    },
                    stop: function () {
                        $searchWrap.removeClass('hide')
                    }
                });
            })(attr)

            $searchContent.append($devLi);
        }

        $searchContent.removeClass('hide');
        $searchWrap.data('focus') && $searchWrap.removeClass('hide');
    };

    Meeting.filterByKeyword = function (confid, keyword) {
        var $meetingBox = $("#meeting_" + confid),
            $searchContent = $meetingBox.find('.search-content'),
            $searchLi = $searchContent.children();

        for(var i = 0, j = 0, len = $searchLi.length; i < len; i++){
            var $devLi = $searchLi.eq(i),
                name = $devLi.find('.search_dev_name').text();
            if(pinyinUtil.getFirstLetter(name, true).join(',').toLowerCase().indexOf(keyword) !== -1 || pinyinUtil.getPinyin(name, '', false, true).join(',').toLowerCase().indexOf(keyword) !== -1 || name.indexOf(keyword) !== -1){
                $devLi.removeClass('hide')
            } else{
                $devLi.addClass('hide');
                j++;
            }
        }

        var handleNoSearch = i === j? 'removeClass': 'addClass';
        $searchContent.next()[handleNoSearch]('hide')
    }

