        }
        else{
            confids = confids.split(",");
            var _json = {};
            _json.confid = confids;
            _json.conftype = conftype;
            _json.starttime = starttime;
            var posturl = "/post/conf/confDel";
            var _msg_confirm = LANG.getDisplayText("meeting_delete_confirm");
            if(_json.confid.length > 1) _msg_confirm = LANG.getDisplayText("meeting_delete_mult_confirm");

            layer.confirm(_msg_confirm, CONFIG.confirmOptions, function(index){
                simpleAjaxSubmit2({"url":posturl,"data":_json,
                    "suc_suc":function(){
                        showMessage(LANG.getDisplayText("del_suc"));
                        for(var i in _json.confid){
                            $("#meeting_"+_json.confid[i]).remove();
                        }
                        if(Meeting.curPageListEmpty()){
                            var pageCur = parseInt(Meeting.config.options.jPageCur.val());
                            if(pageCur > 0) pageCur--;
                            Meeting.config.options.jPageCur.val(pageCur);
                        }
                        Meeting.search(Meeting.config.options);
                    }
                });
            });//confirm
        }
