		var isFav = jThis.hasClass("favOn");//是否已经是收藏的会议了，如果是，则isFav = true;
		var posturl = isFav ? "/post/conf/confCancelFavorites" : "/post/conf/confAddFavorites";

		var _json = {};
		_json.confid = meeting_id;
		_json.conftype = meeting_conftype;
		_json.starttime = meeting_starttime;

            simpleAjaxSubmit2({"url":posturl,"data":_json,
                "suc_suc":function(){
					var jCheckBox = jParent.find('.checkbox');
					//取消收藏
                    if(isFav){
                        jThis.removeClass("favOn");
                        showMessage(LANG.getDisplayText("meeting_fav_cancel_suc"));
                        //取消收藏，如果当前页面是收藏会议列表，需要删除该会议
                        if(meeting_conftype == "3"){
                            $("#meeting_"+meeting_id).remove();
                            if(Meeting.curPageListEmpty()){
                                Meeting.search(Meeting.config.options);
                            }
						}
						jCheckBox.removeClass('v_hidden');
                    }
                    //收藏会议
                    else{
                        jThis.addClass("favOn");
						jCheckBox.addClass('v_hidden');
                        showMessage(LANG.getDisplayText("meeting_fav_add_suc"));
                    }
                }
            });
