    Meeting.Control.checkbox = function(jThis,msg){
        var jMeetingBox = $(".meeting_box");
        if(jThis.hasClass("confCheckAll")){
            jMeetingBox.find(".checkbox.v_visible").prop("checked", jThis.prop("checked"));
        }
        else{
            var jCheckBox = jMeetingBox.find(".checkbox.v_visible");
            var len = jCheckBox.length;
            var lenChecked = jMeetingBox.find(".checkbox:checked").not(".confCheckAll").length;
            $(".confCheckAll").prop("checked", len == lenChecked);
        }

        var jChecked = jMeetingBox.find(".checkbox:checked").not(".confCheckAll");
        var ids = "";
        for(var i = 0; i < jChecked.length; i++){
            if(i == 0)
                ids += jChecked.eq(i).parent().find(".meeting_id").val();
            else
                ids += ","+jChecked.eq(i).parent().find(".meeting_id").val();
        }
        ids += "";
        $(Meeting.config.idConfIds).val(ids);
    };

