    Meeting.Info.network = function(jThis){
        var meeting_id = jThis.parent().find(".meeting_id").val();
        var posturl = "/post/conf/confNetStatus";
        var _json = {};
        _json.conferenceID = meeting_id;

        simpleAjaxSubmit2({"url":posturl,"data":_json,"isShowLoading":false,"isShowAjaxError":false,"isShowSucError":false,
            "suc_suc":function(data){
                var _data = data.data.netstat;
                $("#meeting_"+meeting_id).find(".kbs").html("");
                for(var i in _data){
                    var jDev = $("#dev_"+_data[i].clientID);
                    var _html = "&#8595"+LANG.getDisplayText("audio")+":"+(_data[i].netOutput)[0]+"<br>";
                    _html += "&#8595"+LANG.getDisplayText("video")+":"+(_data[i].netOutput)[2]+"<br>";
                    _html += "&#8593"+LANG.getDisplayText("audio")+":"+(_data[i].netInput)[0]+"<br>";
                    _html += "&#8593"+LANG.getDisplayText("video")+":"+(_data[i].netInput)[2];
                    jDev.find(".kbs").html(_html);
                }
                var msStatus = data.data.msStatus || 0;
                $(".ms_status[confid="+meeting_id+"]").html(LANG.getDisplayText("meeting_status")[msStatus]);
            },
            "suc_err":function(){
                $("#meeting_"+meeting_id).find(".kbs").html("");
            },
            "error":function(){
                $("#meeting_"+meeting_id).find(".kbs").html("");
            },
            "complete":function(XHR, TS){
                if(TS == "timeout")
                    $("#meeting_"+meeting_id).find(".kbs").html("");
            }
        });

        var selectedMCU = $('.selectedOrgNav', '#meeting_' + meeting_id),
            info = selectedMCU.data('info'),
            platformId = info.platformId;

        if(!platformId) {//platformId为''则为本机，否则为下级MCU，需要跨级请求
            posturl = "/post/conf/getMemberStatus";
            _json = {};
            _json.confid = meeting_id;
            simpleAjaxSubmit2({
                "url": posturl,
                "data": _json,
                "isShowLoading": false,
                "isShowAjaxError": false,
                "isShowSucError": false,
                "suc_suc": function (data) {
                    var _data = data.data.members;
                    for (var i in _data) {
                        var __data = _data[i];
                        var jDev = $("#dev_" + __data.clientID);
                        var name = __data.name;
                        jDev.find(".name").text(name);

                        var isOwner = __data.isOwner;
                        var isRegistered = __data.isRegistered;// 在线 离线
                        var hasAttended = __data.hasAttended;// 是否入会
                        var isPresenting = __data.isPresenting;
                        var isMicOpen = __data.isMicOpen;
                        var isSpeakerOpen = __data.isSpeakerOpen;

                        if (isOwner) {
                            jDev.addClass("m");
                            jDev.find(".chairmanOn").show();
                            jDev.find(".chairmanOff").hide();
                        }
                        else {
                            jDev.removeClass("m");
                            jDev.find(".chairmanOn").hide();
                            jDev.find(".chairmanOff").show();
                        }

                        if (isRegistered) {
                            jDev.removeClass("off");
                        }
                        else {
                            jDev.addClass("off");
                        }

                        if (hasAttended) {
                            jDev.addClass("in");
                        }
                        else {
                            jDev.removeClass("in");
                        }

                        if (isMicOpen) {
                            jDev.find(".status_microphoneOn").show();
                            jDev.find(".microphoneOn").show();
                            jDev.find(".status_microphoneOff").hide();
                            jDev.find(".microphoneOff").hide();
                        }
                        else {
                            jDev.find(".status_microphoneOn").hide();
                            jDev.find(".microphoneOn").hide();
                            jDev.find(".status_microphoneOff").show();
                            jDev.find(".microphoneOff").show();
                        }

                        if (isSpeakerOpen) {
                            jDev.find(".status_speakerOn").show();
                            jDev.find(".speakerOn").show();
                            jDev.find(".status_speakerOff").hide();
                            jDev.find(".speakerOff").hide();
                        }
                        else {
                            jDev.find(".status_speakerOn").hide();
                            jDev.find(".speakerOn").hide();
                            jDev.find(".status_speakerOff").show();
                            jDev.find(".speakerOff").show();
                        }

                        if (isPresenting) {
                        }
                        else {
                        }

                    }
                },
                "suc_err": function () {
                },
                "error": function () {
                },
                "complete": function (XHR, TS) {
                }
            });
        }
    };

