    //flag：true 设置主席 OprType=10; false 取消主席 dev_id 写空
    Meeting.Dev.chairman = function(jThis,flag,msg){
        var jParent = jThis.parent();
        var dev_id = jParent.find(".dev_id").val();

        if('12|13|14'.indexOf(dev_id.slice(0, 2)) !== -1){
            layer.msg(LANG.getDisplayText("lowerMCUCant"))
            return
        }

        var meeting_id = jParent.parent().parent().parent().find(".meeting_id").val();
        var meeting_conftype = jParent.parent().parent().parent().find(".meeting_conftype").val();
        var posturl = "/post/conf/confControlAttendee";
        var _json = {};
        _json.confid = meeting_id;
        _json.conftype = meeting_conftype;
        _json.devid = flag ? [dev_id] : [];
        _json.OprType = 10;

        simpleAjaxSubmit2({"url":posturl,"data":_json,"isShowLoading":false,
            "suc_suc":function(){
                //设置主席 成功
                if(flag){
                    //取消其他会场的主席标识，并设置自己为主席
                    jParent.siblings().removeClass("m").end().addClass("m");
                    //对全部会场 隐藏 取消主席 控制按钮
                    jParent.parent().find(".chairmanOn").hide();
                    //对全部会场 显示 设置主席 控制按钮
                    jParent.parent().find(".chairmanOff").show();
                    //对自己会场 显示 取消主席 控制按钮
                    jParent.find(".chairmanOn").show();
                    //对自己会场 隐藏 设置主席 控制按钮
                }
                //取消主席 成功
                else{
                    jParent.find(".chairmanOff").show();
                    jParent.removeClass("m");
                }
                jThis.hide();
            }
        });
    };

