    //多画面初始化 根据layout生成模板dom，然后触发index的click事件
    Meeting.initLayout = function(layout, index){
        var len = layout.length;
        var _html = '';
        for(var i = 0; i < len; i++){
            var _data = layout[i];
            var _len = _data.length;
            _html += '<div class="screenMode layout'+_len+'" data-id="'+i+'">';
            _html += '<div class="out">';

            for(var j = 0; j < _len; j++){
                var _innerClass = "";
                if(_data[j][2] > 0) _innerClass = "inner";

                var _margin = '';
                var _zIndex = (_data[j][2]+1);
                //if都是特殊处理
                //i 是模板的index，j是模板中的第几个分割
                if((i==18 && j==1) || (i==18 && j==2)|| (i==18 && j==3) || (i==18 && j==5) || (i==18 && j==7) || (i==20 && j==3) || (i==20 && j==4) || (i==20 && j==5) || (i==21 && j==8) || (i==21 && j==10) || (i==22 && j==1) || (i==22 && j==2)|| (i==22 && j==3) || (i==22 && j==5) || (i==22 && j==7)){
                    _zIndex = (_data[j][2]+1)-1;
                }
                if((i==22 && j==4) || (i==22 && j==11)|| (i==22 && j==14) || (i==22 && j==17)){
                    _zIndex = (_data[j][2]+1)-1;
                }
                _html += '<div class="in '+_innerClass+'" style="z-index:'+_zIndex+'; left: '+(_data[j][0]*100)+'%; top: '+(_data[j][1]*100)+'%; width: '+(_data[j][3]*100)+'%; height: '+(_data[j][4]*100)+'%; ' + _margin + '"></div>';
            }

            _html += '</div></div>';
        }
        $(Meeting.config.idPlaceLayout).html(_html+'<div class="h15"></div>');
        $(Meeting.config.idPlaceLayout).find(".screenMode").eq(index).trigger("click");
    };

    //get 视频流的分辨率类型：主流2、辅流3
    Meeting.getStreamType = function(w,h){
         return 2;
    };

    //从拖入方获得ssrc
    Meeting.getSsrcs = function(jDrops){
        var val = "";
        jDrops.each(function(){
            var _val = "";
            var jThis = $(this);
            if(jThis.hasClass("have")){
                _val = jThis.find(".places_dropable").attr("data-id");
                _val = parseInt(_val) * 10 + Meeting.getStreamType(jThis.width(),jThis.height());
            }

            if(val == ""){
                val += '['+_val+']';
            }
            else{
                val += "," + '['+_val+']';
            }
        });
        val = '['+val+']';
        return val;
    };

    //从 隐藏的 attendee 输入框 的 data-value 属性值 来获得选中设备，这样不用每次都循环所有设备
    Meeting.getDevs = function(dataValue){
        var val = "";
        var _valArr = dataValue.split(",");
        var _len = _valArr.length - 1;
        for(var i = 1; i < _len; i++){
            if(val == ""){
                val += '[{"devid":"'+_valArr[i]+'"}';
            }
            else{
                val += ',{"devid":"'+_valArr[i]+'"}';
            }
        }
        if(val == "") val += "[";
        val += "]";
        return val;
    };

    // name 现在不需要了，原来暂时用来显示设备名
    Meeting.addDev = function(jThis,devid,name,defaultDevs){
        var arrDevs = eval('('+jThis.val()+')');
        var arrDefaultDevs;
        if(typeof(defaultDevs) == "string") arrDefaultDevs = eval("("+defaultDevs+")");
        else arrDefaultDevs = defaultDevs;
        var flag = false;
        for(var i in arrDefaultDevs){
            if(arrDefaultDevs[i].devid == devid){
                arrDevs.push(arrDefaultDevs[i]);
                flag = true;
                break;
            }
        }
        if(!flag){
            arrDevs.push({"devid":devid});
        }

        var str = "[";
        for(var i in arrDevs){
            if(str == "["){
                str += JSON.stringify(arrDevs[i]);
            }
            else{
                str += "," + JSON.stringify(arrDevs[i]);
            }
        }
        str += "]";
        return str;
    };

    Meeting.delDev = function(jThis,devid){
        var arrDevs = eval('('+jThis.val()+')');
        var index = -1;
        for(var i in arrDevs){
            if(arrDevs[i].devid == devid){
                arrDevs.splice(i,1);
                break;
            }
        }
        var str = "[";
        for(var i in arrDevs){
            if(str == "["){
                str += JSON.stringify(arrDevs[i]);
            }
            else{
                str += "," + JSON.stringify(arrDevs[i]);
            }
        }
        str += "]";
        return str;
    };

    Meeting.checkDevConflict = function(options,callback,devCallback){
        if(Meeting.config.ajaxCheckDevConflict != null)
            Meeting.config.ajaxCheckDevConflict.abort();

        if(Meeting.config.isNeedCheckDevConflict){
            var _starttime = options.starttime;
            var _endtime = options.endtime;
            //if 结束时间 为空，提示先选择结束时间
            if(_starttime == "" || _endtime == ""){
                showMessage(LANG.getDisplayText("meeting_time_wrong"));
                return false;
            }
            options = JSON.stringify(options);
            //判断该会场是否冲突
            Meeting.config.ajaxCheckDevConflict = $.ajax({
                type:"POST",
                url:"/post/conf/confGetAttendee",
                data:{"data":options},
                success:function(data,e){
                    if(data.result == 1){
                        var _data = data.data;
                        if(_data != undefined && _data.length > 0){
                            if(devCallback){
                                devCallback();
                            }
                            var message = LANG.getDisplayText("meeting_dev_conflict")+'：<div>';
                            for(var i in _data){
                                var _yystarttime = _data[i].starttime;
                                var _yyendtime = _data[i].endtime;
                                var flagS1 = compareStrDateTime(_starttime,_yystarttime);
                                var flagE1 = compareStrDateTime(_endtime,_yystarttime);
                                var flagS2 = compareStrDateTime(_starttime,_yyendtime);
                                if(flagS1 == 0 || flagE1 == 0 || flagS2 == 0
                                    || (flagS1 == true && flagE1 == false)
                                    || (flagS1 == false && flagS2 == true)
                                    ){
                                    message += '<span style="color:red">'+_data[i].starttime + " - " + _data[i].endtime + '</span><br />';
                                }
                                else{
                                    message += _data[i].starttime + " - " + _data[i].endtime + '<br />';
                                }
                            }
                            message += '</div>';
                            message += '<style>.layui-layer-content{padding:10px 20px;}.layAlertContent{text-align: left;}</style>';
                        }
                        else{
                            callback();
                        }
                    }
                    else{
                        showMessage(LANG.getDisplayText("ajax_error"),{shift:6});
                    }
                },
                error:function(){
                }
            });//ajax
        }
        else{
            callback();
        }
    };

