    Meeting.searchStackList = function(){
       var options = {
            "posturl":"/post/conf/confGetMSStat",
            'emptyHtml':'<div class="get_none"><i></i>'+LANG.getDisplayText("meeting_stack_none")+'</div>'
        };
        $.extend(Meeting.config.options, options);
        listSearch(Meeting, Meeting.renderStackList);
    };

    Meeting.renderStackList = function(data,options){

        var jList = options.jList;
        var emptyHtml = options.emptyHtml;

        jList.html("");

        var msServers = data.msServers;
        //排序
        if(msServers){
            sortList(msServers, "mscServiceName");
        }
        var unallocatedConferences = data.unallocatedConferences;
        var mscServiceNames = [];
        for(var i=0;i<msServers.length;i++) {
            // var _data = msServers[i];
            // mscServiceNames.push(_data.mscServiceName);
            var _data = msServers[i];
            var obj = {}, msName = "", _mscServiceName, mcuName;
			var _mscServerName = _data.mscServiceName;
            if (_data.MSName) msName = _data.MSName;
            mcuName = msName + '('+ _mscServerName.substr(6)+')';
            obj[_mscServerName] = mcuName;
            mscServiceNames.push(obj);
        }
        for(var i=0;i<msServers.length;i++) {
            var _data = msServers[i];
            renderItems(_data, options, mscServiceNames);
        }
        if(unallocatedConferences.length > 0){
            var obj = {
                mscServiceName: LANG.getDisplayText("meeting_not_enabled"),
                allocatedConferences: unallocatedConferences
            }
            renderItems(obj, options, mscServiceNames);
        }

        bindStackSelect();//绑定下拉框的值

        //设置下拉框选中的值
        for(var i=0;i<msServers.length;i++) {
            var allocatedConferences = msServers[i].allocatedConferences;
            var mscServiceName = msServers[i].mscServiceName;
            for(var j=0;j<allocatedConferences.length;j++){
                var confid = allocatedConferences[j].conferenceID;
                $(".stacktype[confid="+confid+"]").val(mscServiceName).trigger("chosen:updated");
            }
        }

        undisableBtn($(Meeting.config.idBtnSearch));
        return true;
    }

    Meeting.getCascadeDeviceFilter = function (confid, keyword, callback) {
        simpleAjaxSubmit2({
            url: '/post/platform/getCascadeDeviceFilter',
            data: {
                confid: confid,
                filterKey: keyword
            },
            isShowLoading: false,
            isShowAjaxError: false,
            isShowSucError: false,
            suc_suc: function(data){
                callback(data);
            }
        })
    };

    function getStackSelectStr(mscServiceNames){
        var str = [];
        str.push("<option value=''>", LANG.getDisplayText("ms_allocation"), "</option>");
        for(var i=0;i<mscServiceNames.length;i++) {
            for (var key in mscServiceNames[i]) {
                var name = mscServiceNames[i][key];
                str.push("<option value=", key, ">", htmlEncode(name), "</option>");
            }
        }
        return str.join("");
    }

    function renderItems(data, options, mscServiceNames){

        var jList = options.jList;
        var _html = "";
        var cbHtml = "";
        var headTitle = data.mscServiceName, msName = "";
        if (data.MSName) msName = htmlEncode(data.MSName);
        var totalCapacity = data.totalCapacity;
        var remainsCapacity = data.remainsCapacity;
        var syl = remainsCapacity === 0? 100: parseInt((totalCapacity - remainsCapacity)/totalCapacity * 100 );
        var width = parseInt(160*(syl/100));
        var visibleHide = "";
        var link = "";
        var linkImg = "";
        if(headTitle == LANG.getDisplayText("meeting_not_enabled")){
            visibleHide = "visibleHide";
            linkImg = 'display:none'
        }else {
            link = 'http://'+headTitle.substr(6).split('/')[0];
            linkImg = "display:inline-block";
            var _mscServerName = data.mscServiceName;
            headTitle = msName + '('+_mscServerName.substr(6)+')';
        }
		_html += '<div class="meeting_box meeting_box_th">'+
					'<div class="head head_hover">' + cbHtml+'<h2 class="fl pr10">'+ '<a class="msclink" style='+linkImg+' href="javascript:window.open(\''+link+'\', \'_blank\')"></a>' + '<span class="title" title="'+headTitle+'">'+headTitle+'</span> '+ '<a class="mscNameEdit '+visibleHide+'" href="javascript:;" title="'+LANG.getDisplayText("meeting_modify")+'"></a>'+'</h2>'+
						'<div class="occupation-count ' + visibleHide + '">' + LANG.getDisplayText("remain_resource") + ' / '+LANG.getDisplayText("total_resource") + ':&nbsp;&nbsp;' + remainsCapacity + ' / ' + totalCapacity + '</div>'+
						'<div class="occupation-wrap ' + visibleHide+'">' + LANG.getDisplayText("use_rate") + '：<span class="tol_bg"><span class="cur_bg" style="width:' + width + 'px;"></span></span>&nbsp;&nbsp;' + syl + '%</div>' +
						'<div class="occupation_invites ' + visibleHide + '">' + '<span class="invites">' + LANG.getDisplayText('ms_invites') + '：' + (data.invites || 0) + '</span>' + '</div>' +
                        '<div class="fr pr20">' + LANG.getDisplayText("operate") + '</div>' +                      
                    '</div>' +
                    '<input id="msName" type="hidden" value="'+_mscServerName+'"/>'+'<input id="modifyName" type="hidden" value="'+msName+'"/>'
				'</div>';

        var allocatedConferences = data.allocatedConferences;
        for(var i = 0; i < allocatedConferences.length; i++){
            var _data = allocatedConferences[i];
            var _starttime = _data.starttime;
            var _endtime = _data.endtime;

            var seDate = Meeting.getHeadMeetingTime(_starttime,_endtime) || [];

            cbHtml = '';
            _data.confid = _data.confid || _data.conferenceID;
            _html += '<div class="meeting_box" id="meeting_'+_data.confid+'"><div class="head">'+cbHtml;
			_html += '<h2 class="fl pr10 ellipsis" title="'+_data.name.replace(/"/g,"&quot;")+'"><span class="title">'+_data.name+'</span></h2>';
			_html += '<div class="fl pr10 ellipsis" style="width: 10%;">' + LANG.getDisplayText('meeting_id_colon') + '：' + _data.confid + '</div>';
            _html += '<div class="tools fr">';
            //TODO
            var optionstr = getStackSelectStr(mscServiceNames);
            _html += '<select class="chosen stacktype" confid="'+_data.confid+'">'+optionstr+'</select>';
            _html += '<input class="meeting_id" type="hidden" value="'+_data.confid+'">';
            _html += '<input class="meeting_confidStarttime" type="hidden" value="'+_data.confid_starttime+'">';
            _html += '<input class="meeting_starttime" type="hidden" value="'+seDate[0]+'"></div>';
            _html += '</div>';

            // 会议详细信息
            _html += '<div class="content">';

			var state = (_data.msPort && _data.msPort > 0) ? LANG.getDisplayText("already_start") : LANG.getDisplayText("not_start");
			_html += '<table class="table1 table_border_none table_not_hover">'+
						'<tr>'+
							'<td style="width: 33%;">'+LANG.getDisplayText("meeting_id_colon")+'：'+_data.confid+'</td>'+
							'<td style="width: 33%;">'+LANG.getDisplayText("conference_resource")+'：' + _data.comsumedCapacity+'</td>'+
							'<td>'+LANG.getDisplayText("meeting_state")+'：<span class="ms_status" confid="'+_data.confid+'" style="color: red;">'+state+'</span></td>'+
						'</tr>';
			if (_data.invitesToMS != undefined) {
				_html += '<tr>' +
							'<td style="width: 33%;">' + LANG.getDisplayText('ms_invitesToMS') + '：' + _data.invitesToMS + '</td>' +
						'</tr>';
			}
            _html += '</table></div></div>';
        }
        jList.append(_html);

    }

    function bindStackSelect(){
        $(".chosen").chosen(CONFIG.chosenOptions);
        $(".stacktype").change(function(e){
            var confid = $(this).attr("confid");
            var _json = {
                "confid": confid,
                "mscServiceName": $(this).val()
            }
            simpleAjaxSubmit2({"url":"/post/conf/confSetMSC","data":_json,
                "suc_suc":function(data){
                    Meeting.searchStackList();
                },
                "suc_err":function(data){}
            });
        });

        $(".stack-list .chosen-container").click(function(e){
            stopPop(e);
        })
    }

    function stopPop(e){
        try
        {
            e.stopPropagation();
        }
        catch(e)
        {
            e.cancelBubble;
        }
    }

    //修改会议，点击应用按钮的更新会议列表
    Meeting.updateApply = function () {
        listSearch(this);
    }
});

