        var _json = _json;
        var $this = _json.$this;
        var jRecordBtnSwit =_json.jRecordBtnSwit;
        var isOpenRecordBtn = _json.isOpenRecordBtn,openClose;
        var isDisplayClose = _json.isDisplayClose;
        var confid = _json.confid;
        var confid_starttime = _json.confid_starttime;
        var devid = _json.devid;
        var stopOrStartRecordFlag = _json.stopOrStartRecordFlag;
        var recordClients = _json.recordClients;
        var json = {
            "confid":confid,
            "confid_starttime":confid_starttime,
            "deviceId":devid,
            "stopOrStartRecordFlag":stopOrStartRecordFlag
        };
        var callback = function(data){
            var result = data.result;
            if(result==1){
                if(isDisplayClose.hasClass('close')){
                    jRecordBtnSwit.css("left", "20px");
                    openClose = "开";
                    recordClients.removeClass('hide');
