                return;
            }

            if(action) {
                var confid = $meeting_box.attr("id").slice(8),
                    devid = $li.attr("devid").slice(-8),
                    devStatus = $li.find(".def_icon").hasClass("hasAttended_icon") ? true : false;
                if(!devStatus && (action !== "previewOper" && action !== "viewSetting" && action !== "endOper")){
                    //没入会的终端只能点视频，画面配置，拨号
                    layer.msg(LANG.getDisplayText("dev_offline_noctrl"));
                    return;
                }
                $this.toggleClass("close").addClass('handled');
                var isClose = $(this).hasClass("close");
                action === "endOper" && $this.prop('title', $this.hasClass('close')? '拨号': '挂断');
                var isClose = $(this).hasClass("close");
