                    if(isClose){
                        $this.parents("li").find(".def_icon").removeClass("owner");
                    }else{
                        $this.parents("li").find(".def_icon").addClass("owner");
                    }
                    controlAction[action](confid, [devid], isClose, canSet);
                }else if (action == "raiseHand") {                    
                    if (isClose) {
                        controlAction[action](confid, [devid], isClose);
                    };                                     
                }else if(action == "voiceMode"){
                    var jVoiceModeBtnSwit = $meeting_box.find(".voiceModeBtnSwit"),
                        isOpenBtn = $meeting_box.find(".isOpenBtn"),
                        isDisplayClose = $meeting_box.find(".voiceModeBtn.close"),
                        openClose;
                    if(isDisplayClose[0]){
                        jVoiceModeBtnSwit.css("left", "0");
                        openClose = "关";
                    }else{
                        jVoiceModeBtnSwit.css("left", "20px");
                        openClose = "开";
                    }
                    isOpenBtn.html(openClose);
                    if(e.isTrigger) {
                        return;
                    }
                    controlAction[action](confid, [devid], isClose, devStatus);
