    /**
     * 更新members状态
     * @param confID
     */
    updateMembers: function(confID, members, info, isNeedShow) {
        var that = meetingControl,
            $confNode = $("#meeting_" + confID),
            $attendul = $confNode.find('.attend_ul'),
            $no_attend = $confNode.find('.no_attend');
            $memberNum = $confNode.find('.memberNum'),
            $handsNum = $confNode.find('.handsNum'),
            handsNum = 0;

        if(!members){
            $no_attend.removeClass('hide').siblings().addClass('hide');
            return
        }

        var len = members.length;
        //会议成员人数
        $memberNum.html(len);
        //创建搜索列表
        var GMeeting = Meeting;

        if(!GMeeting.devName){
            GMeeting.devName = {};
		}
		var devName = GMeeting.devName;
        var key = info?confID +  '_' + info.remotePlatformID + '_' + info.params.confid: '';
		var confData = devName[key || confID] = [];
        //创建attdenli
        var hashConfID = '#' + confID;
		for(var i = len - 1, j = 0; i >= 0; i--) {
			var devData = members[i];
			var clientID = (key ? key + '_' : '') + devData.clientID;
			var orgid = devData.orgid || '001';
			// if (!clientID.slice(-8).match(/^1[3|4]/)) {//不匹配说明是终端不是MCU
			var $devLi = $attendul.find(hashConfID + clientID).removeClass('hide');

			if ($devLi.length === 0) {
				$devLi = $('#J_cloneAttend li').clone().attr({'devId': clientID, 'orgid': orgid, 'id': confID + clientID});
				that.initDrag($devLi);
			}

			that.setDevLiStatus($devLi, devData);
            if (!devData.isMicRequest ) {                    
                $devLi.addClass('noHand');
            } else {
                handsNum++;
                $devLi.removeClass('noHand');
            };
			if (clientID.slice(-8).match(/^1[3|4]/)) {
				$devLi.addClass('hide');
				j ++;
			}
			$attendul.prepend($devLi);
            // } else {
			// 	j++;
			// }
			//推入搜索列表
            var params = {};
            params[clientID] = devData.name;
            confData.push(params);
        }
        
        //举手人数
        $handsNum.html(handsNum);

        //隐藏不在会议中的终端li
        var $li = $attendul.children(),
            liLen = $li.length - len + j;
		for (i = len; i < $li.length; i++) {
            var $dom = $li.eq(i);
            $dom.addClass('hide');
        }

        if(len === j){
            $no_attend.removeClass('hide').siblings().addClass('hide');
            return
        } else {
            $attendul.removeClass('hide').siblings().addClass('hide');
        }

		//点击默认选中的li
		// var selectedNode = that.zTreeObj[confID].getSelectedNodes()[0];
		var selectedNode = that.getSelectedNode(confID);
		var orgid = selectedNode.orgid || selectedNode.platformId;
		var selectedData = $confNode.data('selectedData');
		var devid = selectedData ? selectedData[orgid] : '';
		if(devid){
			var selectLi = $li.filter('#' + devid);
			meetingControl.clickLi(selectLi);
			isNeedShow && that.scrollView(selectLi);
		} else {
			$li.filter(':visible').eq(0).click();
        }

        //更新申请发言列表
        var isShowHand = $(".tabList .tab.cur").attr("isshow");
        if (isShowHand && isShowHand == "true") {
            $('.attend_ul').children().show();
            $('.attend_ul').find('.noHand').hide();
        }
	},
	
