	initSlider: function(ele, options) {
		var _options = {
			step: 1, 
			min: 0, 
			max: 100,
			range: 'min',
			slide: function( event, ui ) {
				var $this = $(event.target);
				var $meetingbox = $this.parents('.meeting_box');
				var $selectAttend = $meetingbox.find('.selected');
				if(!$selectAttend.is(':visible')){
					return;
				}
				var value = ui.value;
				$this.next('.meeting_ctrl_value').text(value);
			},
			stop: function( event, ui ) {
				var that = meetingControl;
				var $this = $(event.target);
				var $meetingbox = $this.parents('.meeting_box');
				var confid = $meetingbox.prop('id').slice(-4);
				var selectedNode = that.zTreeObj[confid].getSelectedNodes()[0];
				var remotePlatformID = selectedNode ? selectedNode.platformId : '';
				var devid = $meetingbox.find('.attend_ul .selected').attr('devid').slice(-8);
				var method = $(event.target).data('method');
				var volume = ui.value;
				var data = {
					body:{ method: method, volume: volume },
					clientID: devid,
					from: "MCUWEB",
					to: devid
				}
				$this.addClass('handled');
				that.forwardSet(data, remotePlatformID)
			}
		};
		$.extend(_options, options);
		ele.slider(_options);
	},

    /**
     *
     * @param data
     * @returns {*|{arrow}|{wrap, hidden}|{dropdown, suggestion}}
     */
     createAttendHtml: function(data) {
        var that = this;
        var node = $(".J_cloneContent").clone(true);
        var html = [];
        var attendee = data.attendee;
        var confid = data.confid;
        that.isLowMCU = that.isLowMCU || {};//保存哪些是级联为下级的会议
        for(var i=0;i<attendee.length;i++){
            var _attendee = attendee[i];
            var devName = _attendee.name ? _attendee.name : " ";
            var devid = _attendee.devid;
			var orgid = _attendee.orgid || '001';
			if(devid){
                if(!devid.match(/^1[2|3|4]/)){//不是12(h323mcu),13(上级mcu).14（下级mcu）开头，则不是MCU，创建li
                    var childNode = $("#J_cloneAttend").clone(true);

                    childNode.find('li').attr({'devid': devid, 'orgid': orgid, 'id': confid + devid}).find('.h_name').text(devName);

                    html.push(childNode.html());
                }
                var devType = devid.substr(0, 2);
                if (devType === '12') {
					that.operForAll(node, false);
                }
                if(devType === '13'){
                    that.isLowMCU[confid] = true;
                }
            }
		}
		var seeOpposite = (data.description && data.description.seeOpposite) ? 1 : 0;
        node.find(".attend_ul").append(html.join(""));
        node.find(".noOwner").val((data.description && data.description.noOwner) ? 1 : 0);
		node.find(".seeOpposite").val(seeOpposite);
		if (seeOpposite == 1) {
			that.operForAll(node, false);
		}
        return node.html();
	 },
	 
