    /**
     * 获取单个终端的带宽
     * clientID限制条件为非空字符串时，忽略netStat条件。
     * netStat为1时，则不返回netInput、netOutput、netInputStat、netOutputStat字段信息。
     */
    updateDevNetStat: function(data, confID, $meetingBox) {
        var that = this;
        var node = $meetingBox;
        var netstat = data.netstat ? data.netstat: [];
        var inputAudio = node.find(".enquiry_width .up_vary").eq(0);
        var inputVideo = node.find(".enquiry_width .up_vary").eq(1);
        var outputAudio = node.find(".enquiry_width .down_vary").eq(0);
        var outputVideo = node.find(".enquiry_width .down_vary").eq(1);
        var $selectedLi = node.find('.attend_ul .selected');
        var $less_bandwidth_warning = node.find('.less_bandwidth_warning');
        var hasAttended;
        if($selectedLi.length !== 0){
            var selectedDevid = $selectedLi.attr('devid').slice(-8);
            hasAttended = $selectedLi.data('status').hasAttended;
        }
        var obj = netstat[0];
        if (hasAttended && obj) {
            var devid = obj.clientID;
            if (devid === selectedDevid) {
                var netInput = obj.netInput; // 上行
                var netOutput = obj.netOutput; // 下行
                var netInputStat = obj.netInputStat;
                var netOutputStat = obj.netOutputStat;
                var lessStat = 0;
                if(netInputStat === 1){
                    inputAudio.addClass('warning');
                    inputVideo.addClass('warning');
                    lessStat = 'netInput_status_less';
                }else{
                    inputAudio.removeClass('warning');
                    inputVideo.removeClass('warning');
                }
                if(netOutputStat === 1){
