    // 传入鼠标X轴位置，初始位置，bar宽度，计算出滑块应该滑动距离（百分比）
    parseLeft: function ( clientX, differ, oWidth ) {
        var sliderX = clientX - differ;

        if(sliderX < 0){
            return 0;
        } else if(sliderX > oWidth){
            return 100;
        }
        var leftP = sliderX / oWidth * 100;
        return Math.round(leftP);
    },
    
