    // getMemberStatus刷出数据时渲染设备状态
    memberInit: function (confid,orgid,isNeedShow, result) {
        var that = meetingControl;
        var orgLen = orgid.length;
        var members = result.data.members;
        var onlineStatus = result.data.onlineStatus;
        var updateMembers = [];
        for (var key in members) {
            if (key.slice(0, orgLen) === orgid) {
                for (var i=0; i<members[key].length; i++) {
                    members[key][i].orgid = key;
                    updateMembers.push(members[key][i]);
                }
            }
        }

        var onlineNum = 0, totalNum = 0;
        for (var key in onlineStatus) {
            if (key.slice(0, orgLen) === orgid) {
                onlineNum += onlineStatus[key].onlineNum;
                totalNum += onlineStatus[key].totalNum;
            }
        }
        $('#meeting_' + confid).find('.meeting_online .num').text(onlineNum);
        $('#meeting_' + confid).find('.meeting_attend .num').text(totalNum);
        that.updateMembers(confid, updateMembers, undefined, isNeedShow);
    },
	// 获取本组织及子孙组织的设备
	getAttendeesByOrgid: function(members, topOrg, platformId) {
		var attendees = [];
		if (!members) {
			return attendees;
		}
		var allAttendees = members.all;
		if (platformId || (topOrg && topOrg == '001')) {
			attendees = allAttendees;
		} else {
			var len = topOrg.length;
			for (var key in members) {
				var member = members[key];
				if (key.slice(0, len) === topOrg) {
					for (var i=0; i<member.length; i++) {
						var index = member[i];
						allAttendees[index].orgid = key;
						attendees.push(allAttendees[index]);
					}
				}
			}
		}
		return attendees;
	},

