    reqAttendDev: function ($this) {//请求设备类型和ip
        var $meetingBox = $this.parents('.meeting_box'),
            clientID = $this.attr('devid').slice(-8),
            $devType = $meetingBox.find('.dev_type'),
            $devId = $meetingBox.find('.dev_id'),
			$devIp = $meetingBox.find('.dev_ip');

		var confid = $meetingBox[0].id.slice(8);
		// var selectedNode = meetingControl.zTreeObj[confid].getSelectedNodes()[0];
		var selectedNode = meetingControl.getSelectedNode(confid);
		
		var remotePlatformID = selectedNode.platformId || '';
        var callback = function (data) {
			if(data){
				var $selectedLi = $meetingBox.find('.attend_ul .selected'),
					ip = '13|14|20|30'.indexOf(data.type) !== -1? data.latestip: data.ip
				if($selectedLi[0] === $this[0]){
					$devType.text(LANG.getDisplayText("devType","select")[data.type]);
					$devId.text(data.devid);
					$devIp.text(ip).attr("title",ip).parent().removeClass('hide');
					$devIp.prop('href', '20'.indexOf(data.type) !== -1? 'http://' + ip: 'javascript:void(0)')
				}
			} else {
				var devType = clientID.substring(0, 2);
				if('90|91'.indexOf(devType) !== -1){
					$devType.text(LANG.getDisplayText("devtype_" + devType));
				} else {
					$devType.text(LANG.getDisplayText("unknown"));
				}
				$devId.text(clientID);
				$devIp.parent().addClass('hide');
			}
		};

        if(remotePlatformID === ''){
            simpleAjaxSubmit2({
                isShowLoading: false,
                isShowSucError: false,
                "url":"/post/dev/devGetById","data":{
                    devid: clientID
                },
                "suc_suc":function(data){
                    callback(data.data[0]);
                }
            });
        } else {
