    // 获取处理过的树节点数据
    getZtreeNodes: function(members) {
        var zTreeNodes = [];
        var zTreeMap = {};
        var orgAllMap = JSON.parse(JSON.stringify(Meeting.orgAllMap));
        for (key in members) {
            var orgid = key;
            if (!zTreeMap[orgid] && orgAllMap[orgid]) {
                zTreeMap[orgid] = orgAllMap[orgid];
            }
            // while (orgid && orgid.length > 0) {
            //     if (!zTreeMap[orgid] && orgAllMap[orgid]) {
            //         zTreeMap[orgid] = orgAllMap[orgid];
            //     }
            //     orgid = orgid.slice(0, -3);
            // }
        }
        for (var key in zTreeMap) {
            var orgJson = zTreeMap[key];
            var topMcuid = $('.mcuid').val();
            if (orgJson.mcuid != topMcuid) {
                orgJson.info = map[orgJson.mcuid];
            }

            // var pId = key.slice(0, -3);
            // if(pId){
            //     orgJson.pId = pId;
            // }
            orgJson.open = true;
            orgJson.id = key;
            orgJson.name = orgJson.orgname;
            orgJson.icon = '/images/org.png';
            if (orgJson.mcuip) {
                if (orgJson.mcustatus && orgJson.mcustatus == 1) {
                    orgJson.icon = '/images/mcu_online.png';
                }else{
                    orgJson.icon = '/images/mcu_offline.png';
                }
            }

            var pId = meetingControl.getPid(key, members);
            if (pId) {
                orgJson.pId = pId;
            }
            zTreeNodes.push(orgJson);
        }
        return zTreeNodes;
    },

    getPid: function (node, _data) {
        var curPid = node.substring(0, node.length - 3);
        if (!$.isEmptyObject(_data) && curPid) {
            for (var key in _data) {
                var orgid = key;
                if (orgid == curPid) {
                    return orgid;
                }
            }
            var node = curPid;
           return meetingControl.getPid(node, _data);
        }
    },
    
