	addHoverDom: function(treeId, treeNode) {
		var platformId = treeNode.platformId;
        var aObj = $('#' + treeNode.tId + '_a');
        if ($('#diyBtn_' + treeNode.id).length > 0) return;
        if(platformId) {
			var ip = treeNode.ip;
			var port = treeNode.port;
			var username = treeNode.username;
			var password = $.md5(treeNode.password);
            var linkAddress = 'http://' + ip + ':' + port + '/autoLogin?username=' + username + '&password=' + (password || '');
            var  str = '<span id="diyBtn_' + treeNode.id + '"><span class="plamformLink" href="'+ linkAddress +'" target="_blank" title="链接">链接</span><span>';
            aObj.prepend(str);
            var btns = $('#diyBtn_' + treeNode.id);
            if (btns) {
                btns.find('.plamformLink').on('click', function(e) {
                    var url = linkAddress;
                    window.open(url)
                    e.stopPropagation();
                });
            }
        } else {
            return ;
        }
	},

	removeHoverDom: function(treeId, treeNode){
        $('#diyBtn_' + treeNode.id).off().remove();
    },

	clickLi: function($li) {
		var that = meetingControl;
        var $this = $li,
            devid = $this.attr("devid").slice(-8),
            isSelected = $this.hasClass('selected'),
            $meeting_box = $this.parents(".meeting_box"),
            confid_starttime =$meeting_box.attr('confid_starttime'),
            isOpenRecordBtn = $meeting_box.find(".isOpenRecordBtn"),
            jRecordBtnSwit = $meeting_box.find(".toRecordBtnSwit"),
            isDisplayClose = $meeting_box.find(".toRecordBtn.close"),
            toRecordBtn = $meeting_box.find(".toRecordBtn"),
            recordClients = $meeting_box.find(".recordClients"),
            confIDs = meetingTimer.getVisiableConfIds();
		var confid = confid_starttime.slice(0, 4);
        if(!isSelected){
            $this.addClass('selected')
                .siblings('.selected').removeClass('selected');
        }
        var _json = {
            "devid":devid,
            "confid_starttime":confid_starttime,
            "jRecordBtnSwit":jRecordBtnSwit,
            "isOpenRecordBtn":isOpenRecordBtn,
            "isDisplayClose":isDisplayClose,
            "toRecordBtn":toRecordBtn,
            "recordClients":recordClients
        }
		meetingControl.updateRecordStatus(confIDs,_json);
