		that.updateSelectStatus($this, isSelected);
		that.updateConfNetStatus(confid, that.updateDevNetStat, devid);
	},

	// 1 全体看主席; 2 全体看主流; 4 常规显示;
	getControlModeById: function(confid) {
		var url = '/post/conf/getControlModeById';
		var param = {
			confid: confid
		};
		simpleAjaxSubmit2({
			url: url,
			data: param,
			isShowLoading: false,
			suc_suc: function(data){
				var _data = data.data;
				var videoMode = _data.videoMode;
				var clientLayout = _data.clientLayout;
				var $meetingBox = $('#meeting_' + confid);
				var obj = {
					1: '.viewOper', // 全体看主席
					2: '.viewMainOper', // 全体看主流
					4: '.viewNormalOper' // 常规显示
				}
				var classStr = obj[videoMode];
				if (clientLayout == 4) {
					classStr = '.viewAssistOper'; // 全体看辅流
				}
				var $oper = $meetingBox.find(classStr);
				$meetingBox.find('.J_disabledMS.operSelected').removeClass('operSelected');
				$oper.addClass('operSelected');
				$meetingBox.find('.viewOperWrap').data('videoMode', videoMode);
			}
		})
	},

    getMeetingPeople: function (confid) {//获取会议在线人数和总人数
        var that = this;
        simpleAjaxSubmit2({
            isShowLoading: false,
            isShowSucError: false,
            url:'/post/platform/getMeetingPeople',
            data:{confid: confid},
            suc_suc: function (data) {
                var data = data.data,
                    $meeting_box = $('#meeting_' + confid);

                $meeting_box.find('.head').data('data', data);

                that.isPresenting = data.present;

                var ownerId = data.owner,
                    $chairmanBox = $meeting_box.find('.meeting_chairman .place_box');
                if(!$chairmanBox.hasClass('handled')){
                    if(ownerId){
                        var $attendLi = $meeting_box.find('li[devid=' + ownerId + ']');
                        meetingControl.setPlaceBox($chairmanBox, ownerId, $attendLi.find('.h_name').text());
                    } else {
                        meetingControl.clearPlaceBox($chairmanBox)
                }
                } else {
                    $chairmanBox.removeClass('handled')
                }

                var called = data.speaker,
                    $calledBox = $meeting_box.find('.meeting_called .place_box');
                var memberStatus = data.memberStatus;
                if(!$calledBox.hasClass('handled')){
                    if(called && called.devid){
                        var remotePlatformID = called.remotePlatformID,
                            calledId = called.devid;
                        if(remotePlatformID){
							var selectedNode = that.zTreeObj[confid] ? that.zTreeObj[confid].getSelectedNodes()[0] : {};
							var remoteConfid = selectedNode.remoteConfID;
							var remotePlatformID = selectedNode.platformId;
                            // var $remoteMCU = $meeting_box.find('.organization-nav[remoteplatformid=' + remotePlatformID + ']'),
                            //     remoteConfid = $remoteMCU.data('info').params.confid;
                            calledId = confid + '_' +  remotePlatformID + '_' + remoteConfid + '_' + called.devid;
                        }
                        for(var i = 0, len = memberStatus.length; i < len; i++){
                            if(calledId === memberStatus[i].clientID){
                                var mName = memberStatus[i].name
                            }
                        }
                        meetingControl.setPlaceBox($calledBox, calledId, called.name || mName);
                    } else {
                        meetingControl.clearPlaceBox($calledBox)
                    }
					$meeting_box.find('.meeting_called .def_icon').removeClass('hasAttended_icon online_icon');
				} else {
                    $calledBox.removeClass('handled')
                }

                $meeting_box.find('.J_disabledMS[ng-action=micOper]').prop('checked', data.bAllMicClose);
                $meeting_box.find('.J_disabledMS[ng-action=speakerOper]').prop('checked', data.bAllSpeakerClose);
                // 用户锁定会议后只有该用户或者高权限的用户可以取消当前会议锁定状态
                var jIsLock = data.isLocking,
                    jLockOper = $meeting_box.find('#lockOper'),
                    isChecked = false, isDisabled = false; //默认不锁定，可勾选状态
                if (!$.isEmptyObject(jIsLock)) {
                    var lockUserId = jIsLock.adminid;
                        isChecked = true;
                } else if (jIsLock == 0) {
                    isChecked = false;
                }
                jLockOper.prop('checked', isChecked);

                if (lockUserId && lockUserId != loginUsername && loginUsername != "admin") {
                    isDisabled = true;
                }
                jLockOper.attr("disabled", isDisabled)
                           
                // $meeting_box.find('.meeting_online .num').text(data.onlineNum);
                // $meeting_box.find('.meeting_attend .num').text(data.totalNum);

                var presentingData = null;
				var memberStatus = data.memberStatus;
				var len = memberStatus.length;
				var flagLen = len;
				// 演示者为下级终端时，会将14模拟终端塞入memberStatus
				for (var j=0; j<len; j++) {
					if (memberStatus[j].clientID.slice(0, 2) == '14') {
						flagLen --;
					}
				}
                for(var i = 0; i < len; i++){
                    var memberData = memberStatus[i],
                        memberDevid = memberData.clientID,
                        $defIcon = null;
                    if(ownerId.indexOf(memberDevid) !== -1 && memberData.isOwner){
                        $defIcon = $meeting_box.find('.meeting_chairman .def_icon')
                    }
                    if(calledId && calledId.indexOf(memberDevid) !== -1){
						// $defIcon = $meeting_box.find('.meeting_called .def_icon')
						var _$defIcon = $meeting_box.find('.meeting_called .def_icon');
						if (flagLen == 1) {
							$defIcon = _$defIcon;
						}
						if (flagLen == 2) { // 主席和点名人，演示者为其中之一
							if (!memberData.isOwner) { // 已知有可能有问题，级联到323上级，只有点名人和演示者
								$defIcon = _$defIcon;
							}
						}
						if (flagLen == 3) { // 主席、点名人、演示者
							if (!memberData.isOwner && !memberData.isPresenting) {
								$defIcon = _$defIcon;
							}
						}
                    }

                    if($defIcon){
                        var hasAttendedFun = memberData.hasAttended? 'addClass': 'removeClass',
                            isRegistered = memberData.isRegistered? 'addClass': 'removeClass';
                        $defIcon[hasAttendedFun]('hasAttended_icon')[isRegistered]('online_icon');
                    }

                    if(memberData.isPresenting){//演示者
                        presentingData = memberData;
                    }
                }
                var node = $meeting_box.find(".meeting_demonstration");
                presentingData ? node.addClass("isPresenting") : node.removeClass("isPresenting");
                if(presentingData){
                    node.find(".presenting_name").text(presentingData.name);
                }
            }
        })
    },

    GetDevVolume: function (dev) {
        var that = this,
			$meetingBox = dev.parents('.meeting_box'),
			confid = $meetingBox.attr('id').slice(8),
			devid = dev.attr('devid').slice(-8);
		// var selectedNode = that.zTreeObj[confid].getSelectedNodes()[0];
		var selectedNode = that.getSelectedNode(confid);
		var remotePlatformID = selectedNode.platformId || '';
        var callback = function (data) {
			var _data = data.data;
			if (_data) {
				var volume = _data['GetVolume'];
				var $selectedLi = $meetingBox.find('.attend_ul .selected');
				if(dev[0] === $selectedLi[0] && volume){
					// var speakerVolume = $meetingBox.find('.speakerVolume.bar')
					var $speakerSlider = $meetingBox.find('.speakerSlider');
					if(!$speakerSlider.hasClass('handled')){
						// var speakerSlider = $meetingBox.find('.speakerSlider'),
						// 	speakerColor = $meetingBox.find('.speakerColor'),
						// 	speakerValue = speakerVolume.next();
						// that.setSliderDetail(volume.speakerVolume, speakerSlider, speakerColor, speakerValue);
						that.setSlider($speakerSlider, volume.speakerVolume || 0);
					} else {
						$speakerSlider.removeClass('handled')
					}
	
					// var micVolume = $meetingBox.find('.micVolume.bar')
					var $micSlider = $meetingBox.find('.micSlider');
					if(!$micSlider.hasClass('handled')){
						// var micSlider = $meetingBox.find('.micSlider'),
						// 	micColor = $meetingBox.find('.micColor'),
						// 	micValue = micVolume.next();
						// that.setSliderDetail(volume.micVolume, micSlider, micColor, micValue);
						that.setSlider($micSlider, volume.micVolume || 0);
					} else {
						$micSlider.removeClass('handled')
					}

					var $gainSlider = $meetingBox.find('.gainSlider');
					if(!$gainSlider.hasClass('handled')){
						that.setSlider($gainSlider, volume.gainVolume == undefined ? -6 : volume.gainVolume);
					} else {
						$gainSlider.removeClass('handled')
					}
				}
			}
		};
		var param = {
			methods: ['GetVolume'],
			toDevid: devid
		}
		that.forwardGet(param, remotePlatformID, callback);
	},

	// forward协议，获取接口 
	forwardGet: function(param, remotePlatformID, callback) {
		if (remotePlatformID) {
			meetingInterface.getCascadeAPI({
				remotePlatformID: remotePlatformID,
				method: '/post/forwards',
				params: param
			}, callback)
		} else {
			simpleAjaxSubmit2({
				isShowLoading: false,
				isShowSucError: false,
				url: '/post/forwards',
				data: param,
				suc_suc: function(data) {
					callback && callback(data);
				}
			})
		}
	},

	// forward协议，设置接口
	forwardSet: function(param, remotePlatformID) {
		if (remotePlatformID) {
			meetingInterface.getCascadeAPI({
				remotePlatformID: remotePlatformID,
				method: "/post/forward",
				params: param
			})
		} else {
			simpleAjaxSubmit2({
				isShowLoading: false,
				isShowSucError: false,
				url: '/post/forward', 
				data: param,
				suc_suc: function() {}
			});
		}
	},

    handleKeywordBlur: function (obj) {
        var $obj = $(obj);
        $obj.siblings('.search-wrap').addClass('hide').data('focus', false);
    },

    getClientMediaStatus: function ($ele) {
        var that = this,
            devid = $ele.parents('.h_body_right').find('.dev_id').text(),
            confid = $ele.parents('.meeting_box').find('.meeting_id').val()
        meetingInterface.postUrl('/post/conf/getClientMediaStatus',{
                confid: confid,
                devid: devid
            }, function (data) {
                var dataJson = data.data[0]
                    , $meetingBox = $('#meeting_' + confid)
                    , $mediaStatus = $meetingBox.find('.mediaStatus')
                    , $no_mediaStatus = $meetingBox.find('.no_mediaStatus')
                    , $dpi = $mediaStatus.find('.dpi')
                    , $frameRate = $mediaStatus.find('.frameRate')
                    , $packetLossRate = $mediaStatus.find('.packetLossRate')

                $dpi.text(dataJson.height)
                $frameRate.text(dataJson.frameRate)
                $packetLossRate.text(dataJson.packetLossRate)

                $no_mediaStatus.addClass('hide')                
                $mediaStatus.removeClass('hide')
            }
        ) 
    },

    getOnlineReason: function ($ele) {
        var that = this,
            devid = $ele.parents('.h_body_right').find('.dev_id').text(),
            confid = $ele.parents('.meeting_box').find('.meeting_id').val();
            // selectedMCU = $('.selectedOrgNav', '#meeting_' + confid),
            // info = selectedMCU.data('info'),
            // slaveConfid = info.params.confid,
			// remotePlatformID = info.remotePlatformID;
		var selectedNode = that.zTreeObj[confid] ? that.zTreeObj[confid].getSelectedNodes()[0] : {};
		var slaveConfid = selectedNode.remoteConfID;
		var remotePlatformID = selectedNode.platformId || '';
        var callback = function(data){
            var data = data.data,
            	errno = data.errno,
            	reason = data.reason,
            	text = '';
            // if(errno === -1){
            //     reason = errno;
            // }
            if(reason === 2){
				var errmsg = data.errmsg;
				text = ')';
				if (errmsg.rtpIPs) {
					text = '. rtpIP: ' + errmsg.rtpIPs.join(', ') + ' port: ' + errmsg.rtpPort + ')';
				}
            }
            if(reason === 3){
                // text = LANG.getDisplayText('onlineReason_' + errno);
				text = errno;
			}

			$ele.text(LANG.getDisplayText('onlineReason_' + reason) + ' ' + text);
        }
        
        if(remotePlatformID === ''){
            meetingInterface.postUrl('/post/conf/getAbsentReason', {
                confid: confid,
                devid: devid
            },
            function(data) {
                callback(data);
            })
        }else{
            var json = {
                remotePlatformID: remotePlatformID,
                method:"/post/conf/getAbsentReason",
                params: {
                    confid: slaveConfid,
                    devid: devid
                }
            }
            meetingInterface.getCascadeAPI(json, function(data) {
                callback(data);
            })
        }
    },
    
    // 会议锁定
    lockMeeting: function (confid, _url) {
        var _json = {
            "confid": confid
        };
        simpleAjaxSubmit2({"url":_url, "data": _json,
            "suc_suc": function (data) {

            }
        })
    }
}

var meetingTimer = {

    sleep: 3 * 1000,

    start: function(){
        if(this.interval){
            this.stop();
        }
        this.interval = window.setInterval(this.updateData, this.sleep);
    },

    stop: function(){
        window.clearInterval(this.interval);
    },

    reset: function(){
        meetingTimer.stop();
        meetingTimer.start();
	},
	
	// 标识位 会议是否正在结束会议
	endFlag: {},

    /**
     * 需要更新的列表
     * @returns {Array}
     */
    getVisiableConfIds: function(){
        var confIDs = [];
        $(".meeting_box").each(function(){
            var id = $(this).attr('id');
			var isEnding = meetingTimer.endFlag[id];
            if(id && !isEnding && $(this).find(".content").is(":visible")) {
                id = id.replace("meeting_", "");
                confIDs.push(id);
            }
        });
        return confIDs;
    },
    /**
     * 更新members状态
     * 更新音视频流量
     */
    updateData: function(){
        if(meetingTimer.interval) {
            //打开的列表都进行请求
			var confIDs = meetingTimer.getVisiableConfIds();
            if(confIDs.length == 0){
                return;
            }
            for (var i = 0; i < confIDs.length; i++) {
				var id = confIDs[i];
				var indexObj = meetingControl.platformIndex;
				if (indexObj[id] == undefined) {
					indexObj[id] = 0
				} else {
					++ indexObj[id];
				}
                meetingControl.getMemberStateData(id);
                meetingControl.updateConfNetStatus(id, meetingControl.updateNetStatus);
				// meetingControl.getConfOrgTree(id, indexObj[id]);
				meetingControl.getMeetingPeople(id);
				meetingControl.getControlModeById(id);
            }
            //meetingControl.updateRecordStatus(confIDs);
        }
    }
}

