            "data": param,
            "suc_suc": function(){
                callback();
            }
        };
        if(ajaxJson && !ajaxJson.isShowLoading){
            json.isShowLoading = false;
        }
        simpleAjaxSubmit2(json);
    },

    getMemberStatus :  function(param, callback){
        var posturl = "/post/conf/getMemberStatus";
        simpleAjaxSubmit2({
            isShowLoading: false,
            isShowSucError: false,
            "url":posturl,"data":param,
            "suc_suc":function(data){
                callback(data);
            }
        });
    },

    getPlatformMemberStatus: function (info, callback) {
        info.method = '/post/conf/getMemberStatus';
        this.getCascadeAPI(info, callback);
    },

    getCascadeAPI: function (data, callback) {//级联会议，穿网请求
        var posturl = "/post/platform/getCascadeAPI";
        simpleAjaxSubmit2({
            isShowLoading: false,
            isShowSucError: false,
            "url":posturl,"data":data,
            "suc_suc":function(data){
                callback && callback(data);
            }
        });
    },

    postUrl: function(posturl, param, callback){
        simpleAjaxSubmit2({
            isShowLoading: false,
            isShowSucError: false,
            "url":posturl,"data":param,
            "suc_suc":function(data){
                callback && callback(data);
            }
        });
    }

}

